INSERT INTO categoria_cnh (idCategoriaCNH, descricao) values ('ACC', 'Habilita pessoas conduzam veículos de duas rodas com até 50 cm3 de cilindrada, as conhecidas “cinquentinhas”');
INSERT INTO categoria_cnh (idCategoriaCNH, descricao) values ('A', 'Habilita a conduzir veículos de duas ou três rodas, com mais que 50 de cilindrada. Além disso, também é possível conduzir os ciclomotores da categoria ACC');
INSERT INTO categoria_cnh (idCategoriaCNH, descricao) values ('B', 'Habilita o condutor a conduzir veículos de quatro rodas com até 3,5 toneladas de peso bruto total e capacidade para até oito passageiros, além do motorista (nove ocupantes no total). Quadriciclos estão inclusos nesta classe');
INSERT INTO categoria_cnh (idCategoriaCNH, descricao) values ('C', 'Habilita o condutor a dirigir todos os tipos de automóveis da categoria B, e também os veículos de carga, não articulados, com mais de 3,5 toneladas de peso bruto total. São exemplos os caminhões, tratores, máquinas agrícolas e de movimentação de carga');
INSERT INTO categoria_cnh (idCategoriaCNH, descricao) values ('D', 'Habilita o condutor a dirigir veículos para o transporte de passageiros que acomodem mais de 8 passageiros. Aqui, entram os ônibus, micro-ônibus e vans. Com ela, o condutor também pode comandar todos os veículos inclusos nos tipos de CNH B e C');
INSERT INTO categoria_cnh (idCategoriaCNH, descricao) values ('E', 'Todos os veículos inclusos nos tipos de CNH B, C e D. Além disso, ele também pode dirigir veículos com unidades acopladas que excedam 6 toneladas. Aqui estão as carretas e caminhões com reboques e semirreboques articulados. Por fim, é necessário ter a carteira E para conduzir carros puxando trailers');
INSERT INTO categoria_cnh (idCategoriaCNH, descricao) values ('AB', '-- sem descrição --');
INSERT INTO categoria_cnh (idCategoriaCNH, descricao) values ('AC', '-- sem descrição --');
INSERT INTO categoria_cnh (idCategoriaCNH, descricao) values ('AD', '-- sem descrição --');
INSERT INTO categoria_cnh (idCategoriaCNH, descricao) values ('AE', '-- sem descrição --');
